import React, { useState, useEffect } from "react";
import axios from "axios";

export default function App() {
  const [businessData, setBusinessData] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(25);

  useEffect(() => {
    axios
      .get(
        `http://localhost:3001/api/data?limit=${itemsPerPage}&offset=${
          (currentPage - 1) * itemsPerPage
        }`
      )
      .then((response) => setBusinessData(response.data))
      .catch((error) => console.error("Error fetching data:", error));
  }, [currentPage, itemsPerPage]);

  const totalPages = Math.ceil(businessData.length / itemsPerPage);

  const handlePageChange = (newPage) => {
    if (newPage >= 1 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="p-3">
      <table className="table table-striped">
        <thead>
          <tr>
            <th scope="col">business_id</th>
            <th scope="col">name</th>
            <th scope="col">address</th>
            <th scope="col">city</th>
            <th scope="col">state</th>
            <th scope="col">postal_code</th>
            <th scope="col">stars</th>
            <th scope="col">review_count</th>
            <th scope="col">is_open</th>
            <th scope="col">categories</th>
          </tr>
        </thead>
        <tbody>
          {businessData.map((business) => (
            <tr key={business.business_id}>
              <td>{business.business_id}</td>
              <td>{business.name}</td>
              <td>{business.address}</td>
              <td>{business.city}</td>
              <td>{business.state}</td>
              <td>{business.postal_code}</td>
              <td>{business.stars}</td>
              <td>{business.review_count}</td>
              <td>{business.is_open}</td>
              <td>{business.categories}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="pagination">
        <button
          onClick={() => handlePageChange(currentPage - 1)}
          disabled={currentPage === 1}
        >
          Previous
        </button>
        <span>
          {" "}
          Page {currentPage} of {totalPages}{" "}
        </span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Next
        </button>
      </div>
    </div>
  );
}
